// For each day, parse the menu.
do {
    for (day, list) in zip(try doc.select("p u strong"), try doc.select("ul")) {
//        print(try! day.text())
//        print(try! list.text())
        let dayNumber = try day.text()
            .components(separatedBy:CharacterSet.decimalDigits.inverted)
            .joined(separator: "")

        let listItems: [Element] = try list.select("li").array()

        let foodList = listItems
            .compactMap { try? $0.text() }
            .filter { !$0.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty }

        guard let date = formatter.date(from: "Abril 2023 \(dayNumber)") else {
            break
        }
        menu[date] = foodList
    }
} catch {
    print(error)
}
